(43, 1, 1, NULL, 39, 566, 572, 2, 'attestation entree formation professionnelle', 'attestation entree formation professionnelle', 'attestation_entree_formation_professionnelle', 'attestation entree formation professionnelle', NULL, NULL, '0', '<p>Je soussigné(e) {{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }} , {{ SOCIETE_RESPONSABLE_QUALITE }} de l\'Organisme de Formation</p><p>{{ SOCIETE_RAISON_SOCIALE }} , certifie que :</p><p><strong>{{ DOSSIER_CIVILITE }} {{ DOSSIER_NOMCOMPLET }}</strong></p><p>né(e) le : <strong>{{ DOSSIER_DATENAISSANCE }}</strong><br>et demeurant au <strong>{{ DOSSIER_ADRESSE }} {{DOSSIER_CP}} {{DOSSIER_VILLE}}</strong></p><p>est entré(e) en formation de <strong>{{ PARCOURS_INTITULE }}</strong><br>Ce parcours de {{ PARCOURS_INTITULE }} jours se déroule du {{ PLANING_DATEDEBUT }} au {{ PLANING_DATEFIN }}<br>&nbsp;</p><p>Fait pour valoir ce que de droit à {{ SOCIETE_VILLE }} , le .</p><p>{{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }}<br>{{ SOCIETE_RESPONSABLE_QUALITE }}<br><br>{{ SOCIETE_CACHET }}</p>', NULL, NULL, '2024-04-17 16:30:37', '2024-04-17 16:31:24', 0, NULL, NULL),
(44, 1, 1, NULL, 39, 566, 571, 2, 'Certificat de scolarité formation étudiante', 'Certificat de scolarité formation étudiante', 'certificat_scolarite_formation_etudiante', 'Certificat de scolarité formation étudiante', NULL, NULL, '0', '<p>Je soussigné(e), {{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }},{{ SOCIETE_RESPONSABLE_QUALITE }}<br>de {{ SOCIETE_RAISON_SOCIALE }}, certifie que :<br><br><strong>{{ DOSSIER_CIVILITE }} {{ DOSSIER_NOMCOMPLET }}</strong><br><br>né(e) le <strong>{{ DOSSIER_DATENAISSANCE }}</strong><br>et demeurant au <strong>{{ DOSSIER_ADRESSE }} {{ DOSSIER_CP }} {{ DOSSIER_VILLE }}</strong></p><p>est inscrit(e) et suit régulièrement les enseignements afin d\'obtenir le certificat/diplôme dans le cadre d’un cursus intitulé :<strong> {{ PARCOURS_INTITULE }} </strong>qui se déroulera du {{ PLANING_DATEDEBUT }} au {{ PLANING_DATEFIN }} dans notre établissement.</p><p><strong>Objectifs De La Formation </strong>:<br>COMPETENCE_A_VALIDER</p><p>Certificat délivré à la demande de l\'intéressé CONDITION_GENRE pour faire valoir ce que de droit.</p><p>Fait à {{ SOCIETE_VILLE }}, le {{ DATE }}.<br><br>{{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }}<br>{{ SOCIETE_RESPONSABLE_QUALITE }}<br><br>{{ SOCIETE_CACHET }}</p>', NULL, NULL, '2024-04-17 16:33:20', '2024-04-17 16:34:00', 0, NULL, NULL),
(45, 1, 1, NULL, NULL, 566, 572, 2, 'Attestation de validation des compétences', 'Attestation de validation des compétences', 'attestation_validation_competences_formation_professionnelle', 'Attestation de validation des compétences', NULL, NULL, '0', '<p>Je soussigné(e) {{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }},<br>en qualité de {{ SOCIETE_RESPONSABLE_QUALITE }} de {{ SOCIETE_RAISON_SOCIALE }},<br>enregistré sous le numéro de déclaration d’activité 11751937075 auprès de la DRIEETS<br>(Direction régionale des entreprises, de la concurrence, de la consommation, du travail et de l’emploi),<br>atteste que :</p><p><strong>{{ DOSSIER_CIVILITE }} {{ DOSSIER_NOMCOMPLET }} Né(E) Le {{ DOSSIER_DATENAISSANCE }}</strong><br>demeurant au {{ DOSSIER_ADRESSE }}<br>{{ DOSSIER_CP }} {{ DOSSIER_VILLE }}<br><strong>A Suivi La Formation : « {{ PARCOURS_INTITULE }} »</strong> qui s\'est déroulée :<br>du {{ PLANING_DATEDEBUT }} au {{ PLANING_DATEFIN }} en {{ PARCOURS_TYPEFORMATION }}, soit un total de Dureeeffective heures dont Dureecentrewithacc heures en centre et {{ PLANNING_DUREESTAGE }} heures en stage pratique</p><p><br><strong>Nature :</strong>action d\'adaptation et de développement des compétences.<br><strong>Objectifs Pédagogiques Mentionnés Dans Le Programme De Formation :</strong><br>{{ PARCOURS_OBJECTIF }}<br>&nbsp;</p><p><br>Fait pour valoir ce que de droit, le {{ DATE }}, à {{ SOCIETE_VILLE }}.<br><br>{{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }}<br>{{ SOCIETE_RESPONSABLE_QUALITE }}<br>{{ SOCIETE_CACHET }}</p>', NULL, NULL, '2024-04-17 16:34:24', '2024-04-17 16:35:02', 0, NULL, NULL);
INSERT INTO `template` (`id`, `created_by_id`, `updated_by_id`, `categorie_id`, `societe_id`, `type_id`, `usage_id`, `api_gestiform_id`, `intitule`, `title`, `code`, `description`, `conseil_regional`, `avec_entete`, `subtitle`, `content`, `default_template`, `signe`, `created_at`, `updated_at`, `archive`, `specifique`, `code_specifique`) VALUES
(46, 1, 52, NULL, 39, 567, 572, 2, 'reglement interieur formation professionnelle', 'reglement interieur formation professionnelle', 'reglement_interieur_formation_professionnelle', 'reglement interieur formation professionnelle', 0, NULL, '0', '<h2>Préambule : Objet et champ d’application du règlement</h2><p>Le présent règlement s’applique à toutes les personnes participantes à une action de formation organisée par . Un exemplaire est remis à chaque stagiaire dans son espace dématérialisé en ligne et par email. Le règlement définit les règles d’hygiène et de sécurité, les règles générales et permanentes relatives à la discipline ainsi que la nature et l’échelle des sanctions pouvant être prises vis-à-vis des stagiaires qui y contreviennent et les garanties procédurales applicables lorsqu’une sanction est envisagée.</p><p>Il détermine également les règles de représentation des stagiaires pour les formations d’une durée supérieure à 500 heures. Toute personne doit respecter les termes du présent règlement durant toute la durée de l’action de formation.</p><h2><strong>SECTION 1 : Règle D’hygiène Et De Sécurité</strong></h2><h3>Article 1 : Principes Généraux</h3><p>La prévention des risques d’accidents et de maladies est impérative et exige de chacun le respect :</p><ul><li>des prescriptions applicables en matière d’hygiène et de sécurité sur les lieux de formation ;</li><li>de toute consigne imposée soit par la direction de l’organisme de formation soit par le constructeur ou le formateur s’agissant notamment de l’usage des matériels mis à disposition.</li></ul><p>Chaque stagiaire doit ainsi veiller à sa sécurité personnelle et à celle des autres en respectant, en fonction de sa formation, les consignes générales et particulières en matière d’hygiène et de sécurité.</p><p>S’il constate un dysfonctionnement du système de sécurité, il en avertit immédiatement la direction de l’organisme de formation.</p><p>Le non-respect de ces consignes expose la personne à des sanctions disciplinaires.</p><h3>Article 2 : Consignes d’incendies</h3><p>Les consignes d’incendie et notamment un plan de localisation des extincteurs et des issues de secours sont affichés à l’entrée des locaux de l’organisme de formation. Le stagiaire doit en prendre connaissance.</p><p>En cas d’alerte, le stagiaire doit cesser toute activité de formation et suivre dans le calme les instructions du représentant habilité de l’organisme de formation ou des services de secours.</p><p>Tout stagiaire témoin d’un début d’incendie doit immédiatement appeler les secours en composant le 18 à partir d’un téléphone fixe ou le 112 à partir d’un téléphone portable et alerter un représentant de l’organisme de formation.</p><h3>Article 3 : Boissons alcoolisés et drogues</h3><p>L’introduction ou la consommation de drogue ou de boissons alcoolisées dans les locaux est formellement interdite. Il est interdit aux stagiaires de pénétrer ou de séjourner en état d’ivresse ou sous l’emprise de drogue dans l’organisme de formation. Les stagiaires auront accès lors des pauses aux postes de distribution de boissons non alcoolisées.</p><h3>Article 4 : Interdiction de fumer</h3><p>Il est formellement interdit de fumer dans les salles de formation et plus généralement dans l’enceinte de l’organisme de formation.</p><h3>Article 5 : Téléphone portable</h3><p>Le stagiaire doit mettre son portable en arrêt ou en mode silencieux pendant les heures de cours.</p><h3>Article 6 : Accident</h3><p>Le stagiaire victime d’un accident - survenu pendant la formation ou pendant le temps de trajet entre le lieu de formation et son domicile ou son lieu de travail – ou le témoin de cet accident avertit immédiatement la direction de l’organisme de formation. Le responsable de l’organisme de formation entreprend les démarches appropriées en matière de soins et réalise la déclaration auprès de la caisse de Sécurité sociale compétente.</p><p>Dans le cadre d’une formation à distance (e-learning), cet article ne s’applique pas.</p><h2><strong>SECTION 2 : Discipline Générale</strong></h2><h3>Article 7 : Assiduité du stagiaire en formation</h3><h4>Article 7.1 : Horaires de formation</h4><p>Les stagiaires doivent se conformer aux horaires fixés et communiqués au préalable par l’organisme de formation. Le non-respect de ces horaires peut entraîner des sanctions. Sauf circonstances exceptionnelles, les stagiaires ne peuvent s’absenter pendant les heures de stage.</p><h4>Article 7.2 : Présence, absences, retards, vacances ou départs anticipés</h4><p><strong>La présence en cours est obligatoire. Chaque jour, le stagiaire doit attester de sa présence via un système de pointage le matin et l’après-midi. </strong>Tout stagiaire qui signerait à la place d\'un autre en imitant sa signature s\'exposerait, nonobstant les mesures pénalisantes appliquées par la Direction de  aux sanctions prévues par la loi française.</p><p><strong>En cas d’absence, de retard ou de départ avant l’horaire prévu, les stagiaires doivent avertir l’organisme de formation et s’en justifier. L’organisme de formation informe immédiatement le financeur de cet événement. </strong>Tout événement non justifié par des circonstances particulières constitue une faute passible de sanctions disciplinaires. De plus, conformément à l’article R6341-45 du Code du travail, le stagiaire – dont la rémunération est prise en charge par les pouvoirs publics – s’expose à une retenue sur sa rémunération de stage proportionnelle à la durée de l’absence.</p><p>Les dates des vacances indiquées par l’organisme de formation, doivent être strictement respectées.</p><p>En cas de nécessité, une demande d\'autorisation d\'absence doit être présentée à la Direction de au moins trois jours à l\'avance, sauf cas de force majeure.</p><h4>Article 7.3 : Formalisme attaché au suivi de la formation</h4><p>Le stagiaire est tenu de renseigner la feuille d’émargement au fur et à mesure du déroulement de l’action de formation. Il peut lui être demandé de réaliser un bilan de la formation.</p><p>A l’issue de l’action de formation, il se voit remettre une attestation de fin de formation et une attestation de présence au stage à transmettre, selon le cas, à son employeur/administration ou à l’organisme qui finance l’action de formation.</p><p>Le stagiaire remet, dans les meilleurs délais, à l’organisme de formation les documents qu’il doit renseigner en tant que prestataire (demande de rémunérationou de prise en charges des frais liés à la formation ; attestations d’inscription ou d’entrée en stage…).</p><h3>Article 8 : Accès aux locaux de formation</h3><p>Sauf autorisation expresse de la direction de l’organisme de formation, le stagiaire ne peut :</p><ul><li>Entrer ou demeurer dans les locaux de formation à d’autres fins que la formation ;</li><li>Y introduire, faire introduire ou faciliter l’introduction de personnes étrangères à l’organisme ;</li><li>Procéder, dans ces derniers, à la vente de biens ou de services.</li></ul><h3>Article 9 : Tenue</h3><p>Le stagiaire est invité à se présenter à l’organisme en tenue vestimentaire correcte et sans signe ostentatoire d\'appartenance à une religion. est signataire de la charte régionale des valeurs de la République et de la laïcité de la Région Ile-de-France et à ce titre, le stagiaire doit en prendre connaissance au démarrage de la formation et en respecter les principes.</p><h3>Article 10 : Comportement</h3><p>Il est demandé à tout stagiaire d’avoir un comportement garantissant le respect des règles élémentaires de savoir vivre, de savoir être en collectivité et le bon déroulement des formations.</p><p><strong>Travail : </strong>Les stagiaires doivent prendre le stage au sérieux afin qu\'il se déroule dans les meilleures conditions possibles pour tous et qu\'ils en tirent tout le profit qu\'ils sont en droit d\'en attendre.</p><p><strong>Langage : </strong>La politesse étant, avec la propreté, le fondement des relations civilisées, nulle parole volontairement insolente envers qui que ce soit ne sera tolérée.</p><p><strong>Respect D\'autrui : </strong>Il s\'étend à la fois aux personnes et à leurs biens.</p><h3>Article 11 : Utilisation du matériel et des logiciels</h3><p>Sauf autorisation particulière de la direction de l’organisme de formation, l’usage du matériel de formation se fait sur les lieux de formation et est exclusivement réservé à l’activité de formation. L’utilisation du matériel à des fins personnelles est interdite.Le stagiaire est tenu de conserver en bon état le matériel qui lui est confié pour la formation. Il doit en faire un usage conforme à son objet et selon les règles délivrées par le formateur.</p><p>Le stagiaire signale immédiatement au formateur toute anomalie du matériel et a la possibilité de créer un « ticket d’anomalie » sur l’icône GLPI placé sur chaque bureau de chaque ordinateur afin de signaler tout type d’anomalie technique.</p><p>Les stagiaires s\'engagent <strong>À Utiliser Le Matériel Sans Modifier Les Configurations Et Sans Ouvrir Les Boîtiers, À Utiliser Les Logiciels Sans Copier Ou Changer Les Paramètres.</strong></p><h4><strong>Codes D’accès Des « Postes Stagiaires » :</strong></h4><p><strong>Identifiant : Stagiaire</strong><br><strong>Mot De Passe : Stagiaire</strong></p><p>Tous les logiciels autres que ceux installés par  ou les formateurs du centre seront supprimés immédiatement après leur découverte.</p><p>Le stagiaire doit récupérer tous ses travaux sur une clé USB en fin de chaque module car les postes sont réinitialisés toutes les semaines. Le centre ne sera pas responsable des pertes des applications des participants après la fin du stage.</p><p>Pour toute dégradation, un dédommagement sera exigé et des mesures pénalisantes appliquées.</p><h3>Article 12 : Codes de l’espace stagiaire et l’espace Learning Management Systemes e-learning</h3><p>Le candidat s’engage à ne pas divulguer ses codes pour accéder à l’espace privé stagiaire de {{ SOCIETE_RAISON_SOCIALE }}. Ces codes sont confidentiels, personnels et sont valables pendant toute la durée de la formation et jusqu’à 3 mois après la fin de la formation<strong>.</strong></p><p>Si le stagiaire souhaite prolonger ses droits d’accès, il/elle devra s’acquitter de la somme de 75 € TTC par année de prolongation.</p><h3>Article 13 : Enregistrements, Propriété intellectuelle</h3><p>Il est rigoureusement interdit, sauf dérogation expresse, d’enregistrer ou de filmer les sessions de formation. Les documents pédagogiques remis pendant les formations sont protégés par les droits d’auteur. Leur reproduction, sans l’autorisation de l’organisme de formation, est formellement interdite. Ces documents ne peuvent être réutilisés que dans un objectif personnel.</p><h3>Article 14 : Responsabilité de l’établissement</h3><p>Il est vivement déconseillé aux stagiaires d\'avoir sur eux des sommes d\'argent importantes ou des objets de valeurs et de les laisser sans surveillance.</p><p>{{ SOCIETE_RAISON_SOCIALE }} décline toute responsabilité en cas de pertes, vols ou détériorations des objets personnels des stagiaires (Y compris vélos et trottinettes laissés à l’extérieur des locaux…).</p><p>{{ SOCIETE_RAISON_SOCIALE }} ne rembourse pas les frais de dossier et de Kit, même en cas d’abandon.</p><h3>Article 15 : Satisfaction</h3><p>En cas de non satisfaction émanant du stagiaire, l’organisme s’engage à chercher pour lui une solution appropriée à ses besoins (rattrapage, cours en e-learning, autorisation d’absence etc). En cas de mécontentement, le stagiaire doit joindre son référent pédagogique pour faire un point sur sa formation et lui faire part des difficultés rencontrées.</p><p>Le stagiaire se voit remettre à la fin de chaque formation une feuille d’évaluation de la formation qu’il doit remplir et remettre au formateur ou directement à l’organisme de formation. Un questionnaire est envoyé régulièrement au stagiaire pendant la formation sous forme d’enquête de satisfaction par courriel, le stagiaire doit également prendre le temps de le remplir.</p><h2><strong>SECTION 3 : Mesures Disciplinaires</strong></h2><h3>Article 16 : Sanctions disciplinaires</h3><p>Tout manquement du stagiaire à l’une des prescriptions du présent règlement intérieur pourra faire l’objet d’une sanction prononcée par le responsable de l’organisme de formation ou son représentant.</p><p>Tout agissement considéré comme fautif pourra, en fonction de sa nature et de sa gravité, faire l’objet de l’une ou l’autre des sanctions suivantes :</p><ul><li>Rappel à l’ordre ;</li><li>Avertissement écrit par le directeur de l’organisme de formation ou par son représentant ;</li><li>Exclusion temporaire de la formation ;</li><li>Exclusion définitive de la formation.</li></ul><p>Les amendes ou autres sanctions pécuniaires sont interdites.</p><p>Le responsable de l’organisme de formation ou son représentant informe de la sanction prise :</p><ul><li>L’Employeur du salarié stagiaire ou l’administration de l’agent stagiaire ;</li><li>Et/ou le financeur du stage.</li></ul><h3>Article 17 : Garanties disciplinaires</h3><h4>Article 17.1 : Information au stagiaire</h4><p>Aucune sanction ne peut être infligée au stagiaire sans que celui-ci ait été informé au préalable des griefs retenus contre lui. Toutefois, lorsqu’un agissement, considéré comme fautif, a rendu indispensable une mesure conservatoire d’exclusion temporaire à effet immédiat, aucune sanction définitive relative à cet agissement ne peut être prise sans que le stagiaire n’ait été au préalable informé des griefs retenus contre lui et éventuellement, que la procédure ci-après décrite ait été respectée.Tout manquement du stagiaire à l’une des prescriptions du présent règlement intérieur pourra faire l’objet d’une sanction prononcée</p><h4>Article 17.2 : Convocation pour un entretien</h4><p>Lorsque le directeur de l’organisme de formation ou son représentant envisage de prendre une sanction, il est procédé de la manière suivante :</p><ul><li>Il convoque le stagiaire – par email ou lettre recommandée avec demande d’accusé de réception ou remise à l’intéressé contre décharge – en lui indiquant l’objet de la convocation ;</li><li>La convocation indique également la date, l’heure et le lieu de l’entretien ainsi que la possibilité de se faire assister par une personne de son choix stagiaire ou salarié de l’organisme de formation.</li></ul><h4>Article 17.3 : Assistance possible pendant l’entretien</h4><p>Au cours de l’entretien, le stagiaire peut se faire assister par une personne de son choix, notamment le délégué de la formation. Le directeur ou son représentant indique le motif de la sanction envisagée et recueille les explications du stagiaire.</p><h4>Article 17.4 : Prononcé de la sanction</h4><p>La sanction ne peut intervenir moins d’un jour franc ni plus de quinze jours après l’entretien. La sanction fait l’objet d’une notification écrite et motivée au stagiaire sous forme d’un email ou d\'une lettre recommandée avec accusé de réception ou remise contre décharge.</p><h2><strong>SECTION 4 : Représentation Des Stagiaires</strong></h2><h3>Article 18 : Organisation des élections</h3><p>Dans les stages d’une durée supérieure à 500 heures, il est procédé simultanément à l’élection d’un délégué titulaire et d’un délégué suppléant au scrutin uninominal à deux tours, selon les modalités suivantes :</p><p>Tous les stagiaires sont électeurs et éligibles sauf les détenus. Le scrutin a lieu, pendant les heures de la formation, au plus tôt 20 heures et au plus tard 40 heures après le début de la formation ;</p><p>Le responsable de l’organisme de formation a la charge de l’organisation du scrutin.</p><p>Il en assure le bon déroulement. Il adresse un procès-verbal de carence, transmis au préfet de région territorialement compétent, lorsque la représentation des stagiaires ne peut être assurée.</p><h3>Article 19 : Durée de mandat des délégués des stagiaires</h3><p>Les délégués sont élus pour la durée du stage. Leurs fonctions prennent fin lorsqu’ils cessent, pour quelque cause que ce soit, de participer à la formation. Si le délégué titulaire et le délégué suppléant ont cessé leurs fonctions avant la fin de la formation, il est procédé à une nouvelle élection.</p><h3>Article 20 : Rôle des délégués des stagiaires</h3><p>Les délégués font toute suggestion pour améliorer le déroulement des stages et les conditions de vie des stagiaires dans l’organisme de formation. Ils présentent toutes les réclamations individuelles ou collectives relatives à ces matières, aux conditions d’hygiène et de sécurité et à l’application du règlement intérieur.</p><h2><strong>SECTION 5 : Dispositions Particulières</strong></h2><h3>Article 21 : En cas de stage pratique prévu au programme</h3><p><strong>Stage En Entreprise :</strong></p><p>Toutes les informations recueillies à l\'occasion du stage pratique en entreprise sont strictement confidentielles et ne peuvent en aucun cas être divulguées. Le stagiaire se déclare lié à cet égard par le secret professionnel.</p><p><strong>Projet De Fin D\'études :</strong></p><p>Le produit réalisé étant une œuvre de collaboration, il sera libre de droit, ainsi tous les participants et formateurs pourront l’utiliser en respectant les réglementations en vigueur.</p><h3>Article 22 : Formation en e-learning ou blended-learning</h3><p>Dans le cadre d’une formation à distance, il n’y a pas de mise à disposition des locaux. Les stagiaires, s’ils sont salariés, dépendent du règlement intérieur de leur entreprise.</p><p>L’organisme de formation ne pourra être tenu pour responsable d’incidents ou d’accidents survenus à distance pendant les heures de formation et en particulier liées à l’utilisation des outils informatiques et Internet ;</p><p>L’organisme de formation met à disposition un outil de travail à distance sur une période donnée durant laquelle les heures de formations prévues initialement lors de la mise en place de la formation doivent être réalisées au minima par le stagiaire. Ce dernier devra au terme de cette période signer obligatoirement une attestation de connexion et remettre tous les travaux exigés au programme, conformément au nombre de projet de chaque parcours de formation. Tout manquement à cette obligation entrainera des sanctions disciplinaires.</p><h3>Article 23 : Dans le cas du passage devant un jury d’examen</h3><p>Dans le cas où une certification ou un examen est prévu, une date de passage sera convenue avec le stagiaire, par courriel, téléphone ou en directe. Le stagiaire recevra par la suite une convocation par courriel ou par courrier. Le stagiaire est tenu de passer la certification ou l’examen.</p><p>En cas de non présentation ou d’échec partiel ou total, les frais de la certification ou de l’examen d’un second passage seront à la charge du stagiaire. Ces frais s’élèvent à 280 € TTC. Ces frais ne s\'appliquent pas aux stagiaires ayant le statut \"demandeur d\'emploi\".<br> </p><p>L’organisme de formation se réserve le droit de modifier les horaires de passage d’examen en prévenant à l’avance ses stagiaires, en cas de force majeure.</p><h3>Article 24 : Cas de perte de documents administratifs</h3><p>Toute demande de reproduction de documents administratifs supplémentaires (diplômes, attestations etc) suite à la perte des précédents, sera facturée 50€TTC</p><h3>Article 25 : Cas de changement de situation</h3><p>Tout stagiaire dont la situation viendrait à changer (adresse, téléphone, emploi…) en cours ou six mois après le stage s’engage à en informer l’organisme de formation. Après la formation, le stagiaire s’engage à répondre aux emails réguliers de suivi (dans l’emploi) sauf demande particulière de sa part.</p><p><strong>SIGNATURE</strong></p><p><strong>Date : Le&nbsp;</strong><b>{{ NOM_PERSONNE }}</b><br><strong>Nom et Prénom : {{ DOSSIER_NOMCOMPLET }}</strong></p><p> </p>', NULL, 1, '2024-04-17 16:35:44', '2025-01-10 13:27:27', 0, NULL, NULL);
INSERT INTO `template` (`id`, `created_by_id`, `updated_by_id`, `categorie_id`, `societe_id`, `type_id`, `usage_id`, `api_gestiform_id`, `intitule`, `title`, `code`, `description`, `conseil_regional`, `avec_entete`, `subtitle`, `content`, `default_template`, `signe`, `created_at`, `updated_at`, `archive`, `specifique`, `code_specifique`) VALUES
(47, 1, 52, NULL, 39, 567, 572, 2, 'règlement intérieur formation étudiante', 'règlement intérieur formation étudiante', 'reglement_interieur_formation_etudiante', 'règlement intérieur formation étudiante', 0, NULL, '0', '<h2>Préambule : Objet et champ d’application du règlement</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLe présent règlement s’applique à toutes les personnes\r\nparticipantes à une action de formation organisée par Doranco\r\nEspace Multimédia. Un exemplaire est remis à chaque étudiant dans\r\nson espace dématérialisé en ligne et par email. Le règlement\r\ndéfinit les règles d’hygiène et de sécurité, les règles\r\ngénérales et permanentes relatives à la discipline ainsi que la\r\nnature et l’échelle des sanctions pouvant être prises vis-à-vis\r\ndes étudiants qui y contreviennent et les garanties procédurales\r\napplicables lorsqu’une sanction est envisagée.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nIl détermine également les règles de représentation des étudiants\r\npour les formations d’une durée supérieure à 500 heures. Toute\r\npersonne doit respecter les termes du présent règlement durant\r\ntoute la durée de l’action de formation.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\n<b>SECTION 1 : Règle d’hygiène et de sécurité</b></h1>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 1 : Principes Généraux       \r\n</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\">\r\nLa prévention des risques d’accidents et de maladies est\r\nimpérative et exige de chacun le respect :<br></p>\r\n<ul><li style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">\r\ndes prescriptions applicables en matière d’hygiène et de sécurité\r\nsur les lieux de formation;</li></ul>\r\n<ul><li style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%;\">\r\nde toute consigne imposée soit par la direction de Doranco Espace\r\nMultimédia soit par le constructeur ou le formateur s’agissant\r\nnotamment de l’usage des matériels mis à disposition.</li></ul>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nChaque étudiant doit ainsi veiller à sa sécurité personnelle et à\r\ncelle des autres en respectant, en fonction de sa formation, les\r\nconsignes générales et particulières en matière d’hygiène et\r\nde sécurité.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nS’il constate un dysfonctionnement du système de sécurité, il en\r\navertit immédiatement la direction de Doranco Espace Multimédia.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLe non-respect de ces consignes expose la personne à des sanctions\r\ndisciplinaires.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 2 : Consignes d’incendies \r\n</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes consignes d’incendie et notamment un plan de localisation des\r\nextincteurs et des issues de secours sont affichés à l’entrée\r\ndes locaux de Doranco Espace Multimédia. L’étudiant doit en\r\nprendre connaissance.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nEn cas d’alerte, l’étudiant doit cesser toute activité de\r\nformation et suivre dans le calme les instructions du représentant\r\nhabilité de Doranco Espace Multimédia ou des services de secours.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nTout étudiant témoin d’un début d’incendie doit immédiatement\r\nappeler les secours en composant le 18 à partir d’un téléphone\r\nfixe ou le 112 à partir d’un téléphone portable et alerter un\r\nreprésentant de Doranco Espace Multimédia.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 3 : Inscription   \r\n</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes candidats ayant satisfait à l’ensemble des épreuves d’accès\r\nà Doranco Espace Multimédia (entretiens, tests, étude du dossier)\r\nsont inscrits et reçoivent une carte d’étudiant et un certificat\r\nde scolarité après avoir :</p>\r\n\r\n\r\n<ul><li style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">\r\nAcquitté leurs droits fixés préalablement ;</li><li style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">\r\nJustifié de leur affiliation à un organisme de sécurité sociale\r\net payé le cas échéant le montant correspondant ;</li><li style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%;\">\r\nJustifié d’une couverture en responsabilité civile (personnelle\r\net parentale) pour les dommages qu’il serait susceptible de causer.</li></ul>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.77cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nA l’issue des résultats de chaque année, les étudiants doivent\r\nremplir une nouvelle demande d’inscription pour l’année\r\nsuivante. La réinscription n’étant pas automatique, ils devront\r\ns’adresser à leur référent administratif pour assurer leur\r\nréinscription.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 4 : Conditions d’admission</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’admission en 1ère année à Doranco Espace Multimédia est\r\nconditionnée par l’obtention du baccalauréat ou de tout autre\r\ndiplôme permettant l’accès à l’enseignement supérieur.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nIl existe sous certaines conditions la possibilité de se présenter\r\ndevant une commission de dérogation. Celle-ci prend une décision en\r\nfonction du parcours et de l’expérience du candidat qui devra\r\ns’engager à obtenir un baccalauréat à l’issue de l’année en\r\ncours. Les demandes de dérogation devront être faites\r\nofficiellement. Toute dérogation accordée fera l’objet d’un\r\ndocument écrit.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nPour l’organisation des cursus par semestre, contenus, conditions\r\nde délivrance des crédits, validations, conditions de passage en\r\nannée supérieure, en cycle supérieur, en option différente,\r\nprésentation aux diplômes, nature des épreuves etc. se rapporter\r\naux programme détaillé de formation.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 5 : Boissons alcoolisés et drogues</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’introduction ou la consommation de drogue ou de boissons\r\nalcoolisées dans les locaux est formellement interdite. Il est\r\ninterdit aux étudiants de pénétrer ou de séjourner en état\r\nd’ivresse ou sous l’emprise de drogue dans l’établissement.\r\nLes étudiants auront accès lors des pauses aux postes de\r\ndistribution de boissons non alcoolisées.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 6 : Interdiction de fumer</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nIl est formellement interdit de fumer dans les salles de formation et\r\nplus généralement dans l’enceinte de l’établissement.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.53cm; line-height: 108%\">\r\nArticle 7 : Téléphone portable</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant doit mettre son portable en arrêt ou en mode\r\nsilencieux pendant les heures de cours.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 8 : Accident</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.39cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant victime d’un accident - survenu pendant la formation\r\nou pendant le temps de trajet entre le lieu de formation et son\r\ndomicile ou son lieu de travail – ou le témoin de cet accident\r\navertit immédiatement la direction de Doranco Espace Multimédia. Le\r\nresponsable de l’établissement entreprend les démarches\r\nappropriées en matière de soins et réalise la déclaration auprès\r\nde la caisse de Sécurité sociale compétente.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDans le cadre d’une formation à distance (e-learning), cet article\r\nne s’applique pas.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\n<b>SECTION 2 : Discipline Générale</b></h1>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\nArticle 9 : Assiduité de l’étudiant en formation</h2>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 9.1 : Horaires de formation</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes étudiants doivent se conformer aux horaires fixés et\r\ncommuniqués au préalable par Doranco Espace Multimédia. Le\r\nnon-respect de ces horaires peut entraîner des sanctions.Sauf\r\ncirconstances exceptionnelles, les étudiants ne peuvent s’absenter\r\npendant les heures de stage.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 9.2 :Présence, absences, retards, vacances ou départs\r\nanticipés</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLa présence en cours est obligatoire. Chaque jour, l’étudiant\r\ndoit attester de sa présence via un système de pointage le matin et\r\nl’après-midi. Tout étudiant qui signerait à la place d\'un autre\r\nen imitant sa signature s\'exposerait, nonobstant les mesures\r\npénalisantes appliquées par la Direction de Doranco Espace\r\nMultimédia, aux sanctions prévues par la loi française.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nEn cas d’absence, de retard ou de départ avant l’horaire prévu,\r\nles étudiants doivent avertir Doranco Espace Multimédia et s’en\r\njustifier. Tout événement non justifié par des circonstances\r\nparticulières constitue une faute passible de sanctions\r\ndisciplinaires.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes dates des vacances indiquées par Doranco Espace Multimédia,\r\ndoivent être strictement respectées.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nEn cas de nécessité, une demande d\'autorisation d\'absence doit être\r\nprésentée à la Direction de Doranco Espace Multimédia au moins\r\ntrois jours à l\'avance, sauf cas de force majeure.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 9.3 : Formalisme attaché au suivi de la formation</h1>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant est tenu de renseigner la feuille d’émargement au\r\nfur et à mesure du déroulement du cours. Il peut lui être demandé\r\nde réaliser un bilan de la formation.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nA l’issue de la formation, il se voit remettre une attestation de\r\nfin de formation et une attestation de présence au stage.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 10 : Accès aux locaux de formation</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nSauf autorisation expresse de la direction de Doranco Espace\r\nMultimédia, l’étudiant ne peut :</p>\r\n<p class=\"western\" style=\"margin-left: 2.15cm; margin-right: 1.99cm; margin-bottom: 0cm; line-height: 109%\">\r\nEntrer ou demeurer dans les locaux de formation à d’autres fins\r\nque la formation ; Y introduire, faire introduire ou faciliter\r\nl’introduction de personnes étrangères à l’organisme ;</p>\r\n<p class=\"western\" style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nProcéder, dans ces derniers, à la vente de biens ou de services.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 11 : Tenue</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant est invité à se présenter à l’organisme en tenue\r\nvestimentaire correcte et sans signe ostentatoire d\'appartenance à\r\nune religion. Doranco Espace Multimédia est signataire de la charte\r\nrégionale des valeurs de la République et de la laïcité de la\r\nRégion Ile-de-France et à ce titre, l’étudiant doit en prendre\r\nconnaissance au démarrage de la formation et en respecter les\r\nprincipes.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 12 : Comportement</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nIl est demandé à tout étudiant d’avoir un comportement\r\ngarantissant le respect des règles élémentaires de savoir vivre,\r\nde savoir être en collectivité et le bon déroulement des\r\nformations.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n<b>Travail : </b>Les étudiants doivent prendre le cursus au sérieux\r\nafin qu\'il se déroule dans les meilleures conditions possibles pour\r\ntous et qu\'ils en tirent tout le profit qu\'ils sont en droit d\'en\r\nattendre.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n<b>Langage : </b>La politesse étant, avec la propreté, le fondement\r\ndes relations civilisées, nulle parole volontairement insolente\r\nenvers qui que ce soit ne sera tolérée.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n<b>Respect d\'autrui : </b>Il s\'étend à la fois aux personnes et à\r\nleurs biens.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 13 : Utilisation du matériel et des logiciels</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nSauf autorisation particulière de la direction de Doranco Espace\r\nMultimédia, l’usage du matériel de formation se fait sur les\r\nlieux de formation et est exclusivement réservé à l’activité de\r\nformation. L’utilisation du matériel à des fins personnelles est\r\ninterdite.L’étudiant est tenu de conserver en bon état le\r\nmatériel qui lui est confié pour la formation. Il doit en faire un\r\nusage conforme à son objet et selon les règles délivrées par le\r\nformateur.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant signale immédiatement au formateur toute anomalie du\r\nmatériel et a la possibilité de créer un « ticket d’anomalie »\r\nsur l’icône GLPI placé sur chaque bureau de chaque ordinateur\r\nafin de signaler tout type d’anomalie technique.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.53cm; line-height: 108%\">\r\nLes étudiants s\'engagent <b>à utiliser le matériel sans modifier\r\nles configurations et sans ouvrir les boîtiers, à utiliser les\r\nlogiciels sans copier ou changer les paramètres.</b></p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.53cm; line-height: 108%\">\r\n<b>Codes d’accès des « postes étudiants » :  Identifiant : \"stagiaire\",  Mot de passe : \"stagiaire\"</b></p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nTous les logiciels autres que ceux installés par Doranco Espace\r\nMultimédia ou les formateurs du centre seront supprimés\r\nimmédiatement après leur découverte.        \r\n</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant doit récupérer tous ses travaux sur une clé USB en\r\nfin de chaque module car les postes sont réinitialisés toutes les\r\nsemaines. Doranco Espace Multimédia ne sera pas responsable des\r\npertes des applications des participants après la fin du stage.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nPour toute dégradation, un dédommagement sera exigé et des mesures\r\npénalisantes appliquées.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 14 : Codes de l’espace étudiant et l’espace Learning\r\nManagement Systemes elearning</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLe candidat s’engage à ne pas divulguer ses codes pour accéder à\r\nl’espace privé étudiant de Doranco Espace Multimédia. Ces codes\r\nsont confidentiels, personnels et sont valables pendant toute la\r\ndurée de la formation et jusqu’à 3 mois après la fin de la\r\nformation<b>.</b></p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nSi l’étudiant souhaite prolonger ses droits d’accès, il/elle\r\ndevra s’acquitter de la somme de 75 € TTC par année de\r\nprolongation. \r\n</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 15 : Enregistrements, Propriété intellectuelle</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nIl est rigoureusement interdit, sauf dérogation expresse,\r\nd’enregistrer ou de filmer les sessions de formation. Les documents\r\npédagogiques remis pendant les formations sont protégés par les\r\ndroits d’auteur. Leur reproduction, sans l’autorisation de\r\nDoranco Espace Multimédia, est formellement interdite. Ces documents\r\nne peuvent être réutilisés que dans un objectif personnel.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 16 : Travaux d’étudiant</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes travaux des étudiants issus des projets, ateliers, exercices,\r\npropositions pédagogiques, sont considérés comme la propriété de\r\nDoranco Espace Multimédia. A ce titre, Doranco Espace Multimédia\r\npourra les utiliser dans une publication, un document, un support\r\nprivé ou public.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 17 : Responsabilité de Doranco Espace Multimédia</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nIl est vivement déconseillé aux étudiants d\'avoir sur eux des\r\nsommes d\'argent importantes ou des objets de valeurs et de les\r\nlaisser sans surveillance.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDoranco Espace Multimédia décline toute responsabilité en cas de\r\npertes, vols ou détériorations des objets personnels des étudiants\r\n(Y compris vélos et trottinettes laissés à l’extérieur des\r\nlocaux…).</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDoranco Espace Multimédia ne rembourse pas les frais de dossier et\r\nde Kit, même en cas d’abandon.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 18 : Satisfaction</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nEn cas de non satisfaction émanant d’un étudiant, Doranco Espace\r\nMultimédia s’engage à chercher pour lui une solution appropriée\r\nà ses besoins (rattrapage, cours en e-learning, autorisation\r\nd’absence etc). En cas de mécontentement, l’étudiant doit\r\njoindre son référent pédagogique pour faire un point sur sa\r\nformation et lui faire part des difficultés rencontrées.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nL’étudiant se voit remettre à la fin de chaque cours une feuille\r\nd’évaluation de la formation qu’il doit remplir et remettre au\r\nformateur ou directement à l’établissement. Un questionnaire est\r\nenvoyé régulièrement à l’étudiant pendant la formation sous\r\nforme d’enquête de satisfaction par email, l’étudiant doit\r\négalement prendre le temps de le remplir.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\n<b>SECTION 3 : Mesures disciplinaires</b></h1>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 19 : Sanctions disciplinaires</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nTout manquement d’un étudiant à l’une des prescriptions du\r\nprésent règlement intérieur pourra faire l’objet d’une\r\nsanction prononcée par le responsable de l’établissement ou son\r\nreprésentant.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nTout agissement considéré comme fautif pourra, en fonction de sa\r\nnature et de sa gravité, faire l’objet de l’une ou l’autre des\r\nsanctions suivantes :</p>\r\n<p class=\"western\" style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\">\r\nRappel à l’ordre ;</p>\r\n<p class=\"western\" style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\">\r\nAvertissement écrit par le directeur de Doranco Espace Multimédia\r\nou par son représentant</p>\r\n<p class=\"western\" style=\"margin-left: 2.15cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\">\r\n;</p>\r\n<p class=\"western\" style=\"margin-left: 2.15cm; margin-right: 8.67cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nExclusion temporaire de la formation ; Exclusion définitive de la\r\nformation.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes amendes ou autres sanctions pécuniaires sont interdites.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\nArticle 20 : Garanties disciplinaires</h2>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 20.1 : Information à l’étudiant</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nAucune sanction ne peut être infligée à l’étudiant sans que\r\ncelui-ci ait été informé au préalable des griefs retenus contre\r\nlui. Toutefois, lorsqu’un agissement, considéré comme fautif, a\r\nrendu indispensable une mesure conservatoire d’exclusion temporaire\r\nà effet immédiat,aucune sanction définitive relative à cet\r\nagissement ne peut être prise sans que l’étudiant n’ait été\r\nau préalable informé des griefs retenus contre lui et\r\néventuellement, que la procédure ci-après décrite ait été\r\nrespectée.Tout manquement de l’étudiant à l’une des\r\nprescriptions du présent règlement intérieur pourra faire l’objet\r\nd’une sanction prononcée</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 20.2 : Convocation pour un entretien</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLorsque le directeur de Doranco Espace Multimédia ou son\r\nreprésentant envisage de prendre une sanction, il est procédé de\r\nla manière suivante :</p>\r\n<p class=\"western\" style=\"margin-left: 2.51cm; margin-right: 0.51cm; text-indent: -0.38cm; margin-bottom: 0.04cm; line-height: 109%\">\r\n Il convoque l’étudiant – par email ou lettre recommandée avec\r\ndemande d’accusé de réception ou remise à l’intéressé contre\r\ndécharge – en lui indiquant l’objet de la convocation ;</p>\r\n<p class=\"western\" style=\"margin-left: 2.51cm; margin-right: 0.51cm; text-indent: -0.38cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n La convocation indique également la date, l’heure et le lieu de\r\nl’entretien ainsi que la possibilité de se faire assister par une\r\npersonne de son choix étudiant ou salarié de Doranco Espace\r\nMultimédia.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 20.3 : Assistance possible pendant l’entretien</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.67cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nAu cours de l’entretien, l’étudiant peut se faire assister par\r\nune personne de son choix, notamment le délégué du stage. Le\r\ndirecteur ou son représentant indique le motif de la sanction\r\nenvisagée et recueille les explications de l’étudiant.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 20.4 : Prononcé de la sanction</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLa sanction ne peut intervenir moins d’un jour franc ni plus de\r\nquinze jours après l’entretien. La sanction fait l’objet d’une\r\nnotification écrite et motivée aux étudiants sous forme d\'un email\r\nou d’une lettre recommandée avec demande d’accusé de réception\r\nou remise contre décharge.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\n<b>SECTION 4 : Représentation des étudiants</b></h1>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 21 : Organisation des élections d’un délégué de groupe</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLe scrutin a lieu, pendant les heures de la formation, au plus tôt\r\n20 heures et au plus tard 40 heures après le début de la formation\r\n;</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLe responsable de l’établissement a la charge de l’organisation\r\ndu scrutin.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 22 : Durée de mandat des délégués des étudiants</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes délégués sont élus pour la durée de la formation. Leurs\r\nfonctions prennent fin lorsqu’ils cessent, pour quelque cause que\r\nce soit, de participer à la formation. Si le délégué titulaire et\r\nle délégué suppléant ont cessé leurs fonctions avant la fin de\r\nla formation, il est procédé à une nouvelle élection.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 23 : Rôle des délégués des étudiants</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLes délégués font toute suggestion pour améliorer le déroulement\r\ndes études et les conditions de vie des étudiants dans\r\nl’établissement. Ils présentent toutes les réclamations\r\nindividuelles ou collectives relatives à ces matières, aux\r\nconditions d’hygiène et de sécurité et à l’application du\r\nrèglement intérieur.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm\">\r\n<b>SECTION 5 : Dispositions particulières</b></h1>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 6.25cm; margin-bottom: 0cm; line-height: 217%\">\r\nArticle 24 : En cas de stage pratique prévu au programme Stage en\r\nentreprise : \r\n</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nToutes les informations recueillies à l\'occasion du stage pratique\r\nen entreprise sont strictement confidentielles et ne peuvent en aucun\r\ncas être divulguées. L’étudiant se déclare lié à cet égard\r\npar le secret professionnel.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.53cm; line-height: 108%\">\r\n<b>Projet de fin d\'études : <br></b>Le produit réalisé étant une œuvre de collaboration, il sera\r\nlibre de droit, ainsi tous les participants et formateurs pourront\r\nl’utiliser en respectant les réglementations en vigueur.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\n<span lang=\"en-GB\">Article 25 : Formation en e-learning ou\r\nblended-learning</span></h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDans le cadre d’une formation à distance, il n’y a pas de mise à\r\ndisposition des locaux. Les étudiants, s’ils sont salariés,\r\ndépendent du règlement intérieur de leur entreprise.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDoranco Espace Multimédia ne pourra être tenu pour responsable\r\nd’incidents ou d’accidents survenus à distance pendant les\r\nheures de formation et en particulier liées à l’utilisation des\r\noutils informatiques et Internet ;</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDoranco Espace Multimédia met à disposition un outil de travail à\r\ndistance sur une période donnée durant laquelle les heures de\r\nformations prévues initialement lors de la mise en place de la\r\nformation doivent être réalisées au minima par l’étudiant. Ce\r\ndernier devra au terme de cette période signer obligatoirement une\r\nattestation de connexion et remettre tous les travaux exigés au\r\nprogramme, conformément au nombre de projet de chaque parcours de\r\nformation. Tout manquement à cette obligation entrainera des\r\nsanctions disciplinaires.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 26 : Dans le cas du passage devant un jury d’examen</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDans le cas où une certification ou un examen est prévu, une date\r\nde passage sera convenue avec l’étudiant, par courriel, téléphone\r\nou en directe. L’étudiant recevra par la suite une convocation par\r\ncourriel ou par courrier. L’étudiant est tenu de passer la\r\ncertification ou l’examen.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nEn cas de non présentation ou d’échec partiel ou total, les frais\r\nde la certification ou de l’examen d’un second passage seront à\r\nla charge de l’étudiant. Ces frais s’élèvent à 280 € TTC.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nDoranco Espace Multimédia se réserve le droit de modifier les\r\nhoraires de passage d’examen en prévenant à l’avance ses\r\nétudiants, en cas de force majeure.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 27 : Cas de perte de documents administratifs</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nToute demande de reproduction de documents administratifs\r\nsupplémentaires (diplômes, attestations etc) suite à la perte des\r\nprécédents, sera facturée 50€ TTC.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm\">\r\nArticle 28 : Cas de changement de situation</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nTout étudiant dont la situation viendrait à changer (adresse,\r\ntéléphone, emploi…) en cours ou six mois après la formation\r\ns’engage à en informer Doranco Espace Multimédia. Après la\r\nformation, l’étudiant s’engage à répondre aux emails réguliers\r\nde suivi (dans l’emploi) sauf demande particulière de sa part.</p>\r\n<h1 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.32cm; line-height: 108%\">\r\n<b>SECTION 6 : Les droits et devoirs en alternance</b></h1>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.31cm\">\r\nArticle 29 : Vos droits</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\">\r\nVous :</p>\r\n<ul><li style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">Bénéficiez du statut de salarié et d\'une période d\'essai    \r\n</li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">de 45 jours, consécutifs ou non, de présence en entreprise en\r\ncontrat d\'apprentissage </li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">de 30 jours en contrat de\r\nprofessionnalisation</li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">Bénéficiez de la protection sociale comme l\'ensemble des salariés\r\n	y compris pendant le temps passé à l\'école.</li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">Avez accès à la convention collective et au DUERP de votre\r\n	entreprise.</li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">Capitalisez ces années de formation comme des années pleines à\r\n	valoir sur votre retraite.</li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">Bénéficiez de la prise en charge par l\'employeur des frais de\r\n	transports en commun domicile /lieu de travail à hauteur de 50%\r\n	dans les mêmes conditions que les autres salariés.</li><li style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%;\">Disposez de 5 jours de congés spécifiques répartis sur la durée\r\n	de la formation (en plus des congés légaux habituels) pour la\r\n	préparation aux examens.</li></ul><p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%\"><br></p><p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 2.49cm; margin-bottom: 0cm; line-height: 109%\"><br></p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.31cm\">\r\nArticle 30 : Vos devoirs</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\">\r\nVous devez :</p><ul><li style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">Vous impliquer dans la formation comme dans l\'entreprise.</li><li style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">Respecter le règlement intérieur de l\'entreprise et de\r\n	l\'établissement de formation.</li><li style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">Tenir à jour le livret de suivi d’apprentissage.</li><li style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">Suivre les enseignements, les travaux dirigés, les travaux\r\n	pratiques et satisfaire au contrôle des connaissances</li><li style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%;\">Transmettre, le cas échéant, les justificatifs d\'absence tels\r\n	qu\'ils sont définis par le code du travail. Les autres absences sont\r\n	définies comme injustifiées.</li></ul><p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0cm; line-height: 109%\"><br></p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.31cm\">\r\nArticle 31 : Les règles de santé et de sécurité au travail</h2>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nQuel que soit votre métier ou votre formation, vous vous exposez à\r\ndifférents risques dans le cadre de vos activités en entreprise :\r\ntrébuchements, glissades, coupures, brûlures, manutentions\r\nmanuelles, etc. \r\n</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nTout nouveau dans l’entreprise vous êtes davantage exposé aux\r\nrisques que vos collègues, notamment du fait de votre manque\r\nd’expérience. Vous pouvez consulter le Document Unique\r\nd’Evaluation des Risques Professionnels de votre entreprise.</p>\r\n<p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 1.06cm; line-height: 109%\">\r\nA votre arrivée, votre employeur ou votre tuteur doit réaliser un\r\naccueil qui consiste à vous présenter l’entreprise, vos collègues\r\nde travail, les règles à respecter dans l’entreprise ainsi que\r\nles différents risques et les mesures de protection à respecter\r\navant de vous affecter à votre poste de travail.</p><p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 1.06cm; line-height: 109%\">Il peut vous remettre à votre arrivée, le livret d’accueil, votre\r\ntenue de travail, vos équipements de protection individuelle (si\r\napplicable). Il doit également mettre à votre disposition les\r\noutils de travail nécessaires à votre activité.</p><p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 1.06cm; line-height: 109%\">Vous recevrez une convocation de la Médecine du Travail pour un\r\nexamen médical d’embauche au plus tard dans les 2 mois qui suivent\r\nl’embauche.</p><p class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.51cm; margin-bottom: 1.06cm; line-height: 109%\">Une liste des travaux interdits est fixée par voie réglementaire et\r\na été réactualisée par le décret 2013-915 du 11 octobre 2013.\r\nCette liste s’applique aux jeunes âgés d’au moins 15 ans et de\r\nmoins de 18 ans.</p>\r\n<h2 class=\"western\" style=\"margin-left: 1.09cm; margin-right: 0.13cm; margin-bottom: 0.31cm\">Article 32 : Le conseil de perfectionnement</h2>\r\n<p class=\"western\" style=\"margin-bottom: 0.53cm; line-height: 109%\">            Le\r\nconseil de perfectionnement est une instance chargée de veiller à\r\nl’organisation et au fonctionnement du CFA (Centre de Formation\r\nd’Apprentis.)</p>\r\n<p class=\"western\" style=\"margin-bottom: 0.53cm; line-height: 109%\">            Placé\r\nsous la présidence de la directrice générale du CFA, Amira EL\r\nHAJEM, le conseil réunit le personnel administratif, pédagogique,\r\nles délégués ainsi que les partenaires entreprises, dans le but\r\nd’examiner et débattre des questions relatives à            l’organisation\r\net au fonctionnement du CFA sur différents points :<br>\r\n<br>\r\n\r\n</p>\r\n<ul><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\"> Le projet pédagogique du centre de formation d\'apprentis</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	Les conditions générales d\'accueil, d\'accompagnement des\r\n	apprentis, notamment des apprentis en situation de handicap, de\r\n	promotion de la mixité et de la mobilité nationale et\r\n	internationale</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	L\'organisation et le déroulement des formations</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	Les conditions générales de préparation et de perfectionnement\r\n	pédagogique des formateurs</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	L\'organisation des relations entre les entreprises accueillant des\r\n	apprentis et le centre \r\n	</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	Les projets de convention à conclure avec des établissements\r\n	d\'enseignement, des organismes de formation ou des entreprises, en\r\n	vue de leur déléguer la réalisation de tout ou partie des\r\n	enseignements ou de mettre à disposition du CFA des équipements\r\n	pédagogiques ou d\'hébergement</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	Les projets d\'investissement</p>\r\n	</li><li><p style=\"text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\n	Les informations publiées chaque année comme les taux d\'obtention\r\n	des diplômes ou titres professionnels, taux de poursuite d\'études,\r\n	taux d\'interruption en cours de formation et taux de rupture des\r\n	contrats, taux d\'insertion professionnelle des apprentis à l\'issue\r\n	de leur formation, valeur ajoutée de l\'établissement</p>\r\n</li></ul>\r\n<p class=\"western\" style=\"margin-left: 0.64cm; text-indent: 0cm; margin-bottom: 0.53cm; line-height: 109%\">\r\nLe conseil de perfectionnement se déroule tous les ans pour assurer\r\nle meilleur fonctionnement de l\'école et sa pleine réussite.</p>\r\n<p class=\"western\" style=\"margin-left: 0cm; margin-right: 0cm; text-indent: 0cm; margin-bottom: 0cm; line-height: 108%\">\r\n<br>\r\n  <p class=\"MsoNormal\" style=\"mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal\"><span style=\"font-size:10.0pt;font-family:\" arial\",sans-serif;=\"\" mso-fareast-font-family:\"times=\"\" new=\"\" roman\";mso-fareast-language:fr\"=\"\">Signature\r\n </span></p>\r\n  </td>\r\n  </p><p class=\"MsoNormal\" style=\"mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal\"><span style=\"font-size:10.0pt;font-family:\" arial\",sans-serif;=\"\" mso-fareast-font-family:\"times=\"\" new=\"\" roman\";mso-fareast-language:fr\"=\"\">{{ PERSONNE_SIGNATURE }}</span></p>', NULL, 1, '2024-04-17 16:36:46', '2025-01-10 13:27:27', 0, NULL, NULL),
(48, 1, 1, NULL, 39, 567, 572, 2, 'document crif', 'document crif', 'document_crif', 'document crif', NULL, NULL, '0', '<p>Vous avez été sélectionné pour intégrer une formation financée par la Région Île-de-France. Nous sommes heureux de vous soutenir dans cette dynamique vers l’emploi.</p><p>En tant que stagiaire de la formation professionnelle, vous êtes acteur de votre parcours tout au long de celui-ci et possédez des droits et des devoirs.</p><p>Au titre de ces derniers, vous vous engagez, par la présente charte, sur les points suivants :</p><p><strong>Mobiliser votre CPF</strong></p><p>Pour rappel, le Compte Personnel de Formation (CPF), accessible via <a href=\"http://www.moncompteformation.gouv.fr/\">www.moncompteformation.gouv.fr</a>, permet d’acquérir des droits à la formation professionnelle. Ces derniers sont inscrits dans un compte d’heures qui suit la personne tout au long de sa vie professionnelle.</p><p>Si votre formation est éligible au Compte Personnel de Formation (CPF), vous vous engagez à mobiliser les heures acquises, la Région intervenant en complément pour financer votre formation.</p><p><strong>Evaluer votre formation</strong></p><p>Vous êtes invité à vous exprimer sur les conditions de réalisation de votre formation lors de visites de représentants de la Région dans le site où vous êtes formé. A l’issue de votre formation, vous vous engagez à évaluer celle-ci via le système ANOTEA, qui vous sera présenté par votre organisme de formation.</p><p>Vous vous engagez, dans ce cadre, à répondre à un court questionnaire d’évaluation qui vous sera adressé par mail ; vous pourrez également, si vous le souhaitez, faire des commentaires sur le déroulement de votre formation. Votre évaluation ainsi que vos commentaires seront publiés sur les sites « La Bonne Formation » et « Défi Métiers ».</p><p><strong>Signaler votre situation au regard de l’emploi six mois après la fin de formation</strong></p><p>Vous vous engagez à indiquer à la Région votre situation au regard de l’emploi, 6 mois après la fin de votre formation. Vous serez interrogé pour cela via un formulaire qui vous sera adressé par mail.</p><p><strong>Rechercher activement un emploi en fin de formation et accepter toute offre raisonnable d’emploi compatible avec vos nouvelles qualifications, conformément à la loi.</strong></p><p><strong>Répondre aux éventuelles sollicitations des partenaires de la Région pour vous proposer un emploi</strong></p><p>Des propositions d’emploi, en lien avec la formation que vous avez effectuée, pourront vous être communiquées directement par email, y compris avant la fin de votre formation.</p><p>Vous autorisez la région à conserver vos coordonnées téléphoniques et électroniques et à toute proposition ou demande de rendez-vous tendant à faciliter votre recherche d’emploi qui pourrait vous être faite dans ce cadre.</p><p><strong>Signature du stagiaire</strong><br>&nbsp;</p><p>Le DATE_SIGNATURE</p><p>&nbsp;</p><p>{{ DOSSIER_NOMCOMPLET }}<br>&nbsp;</p><p>SIGNATURE_DOSSIER</p>', NULL, NULL, '2024-04-17 16:38:00', '2024-04-17 16:38:37', 0, NULL, NULL);
INSERT INTO `template` (`id`, `created_by_id`, `updated_by_id`, `categorie_id`, `societe_id`, `type_id`, `usage_id`, `api_gestiform_id`, `intitule`, `title`, `code`, `description`, `conseil_regional`, `avec_entete`, `subtitle`, `content`, `default_template`, `signe`, `created_at`, `updated_at`, `archive`, `specifique`, `code_specifique`) VALUES
(49, 1, 1, NULL, 39, 567, 572, 2, 'contrat_FPC_personne_physique_NEW', 'contrat_FPC_personne_physique_NEW', 'contrat_FPC_personne_physique_NEW', 'contrat_FPC_personne_physique_NEW', NULL, NULL, '0', '<p style=\"margin-bottom: var(--spacer-2);\">Le présent contrat règle les rapport entre :<br></p><table><tbody><tr><td><ul><li><b>L’organisme de formation :</b></li></ul><div><table><tbody><tr><td><strong>Nom&nbsp;: </strong></td><td>{{SOCIETE_RAISON_SOCIALE}}</td></tr><tr><td><strong>Adresse :</strong></td><td>{{SOCIETE_ADRESSE}} </td></tr><tr><td><strong>Ville&nbsp;:</strong></td><td>{{SOCIETE_VILLE}}</td></tr><tr><td><strong>CP&nbsp;:</strong></td><td>{{SOCIETE_CP}} </td></tr><tr><td><strong>Tél :</strong></td><td>{{SOCIETE_TEL}}</td></tr><tr><td><strong>Courriel&nbsp;:</strong></td><td>{{{SOCIETE_EMAIL}}</td></tr><tr><td><strong>N° SIREN :</strong></td><td>{{SOCIETE_SIREN}}</td></tr><tr><td><strong><br></strong></td><td><br></td></tr><tr><td><strong>Représenté par :</strong></td><td>&nbsp;{{SOCIETE_RESPONSABLEECOLE_CIVILITE }}{{ SOCIETE_RESPONSABLEECOLE_NOMCOMPLET}}<strong>,</strong> en qualité de&nbsp;{{SOCIETE_RESPONSABLEECOLE_QUALITE}}</td></tr></tbody></table></div><table><tbody><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><p><strong>Ci-après dénommée l’Organisme de Formation.</strong></p><p>&nbsp;</p><table><tbody><tr><td><ul><li><strong>Le(la) stagiaire :</strong></li></ul></td></tr><tr><td><table><tbody><tr><td><strong>Civilité :</strong></td><td><strong></strong>{{DOSSIER_CIVILITE}}</td></tr><tr><td><strong>Nom de famille :</strong></td><td>{{DOSSIER_NOM}}</td></tr><tr><td><strong>Prénom : </strong></td><td>{{DOSSIER_PRENOM}}</td></tr><tr><td><strong>Adresse&nbsp;:</strong></td><td><strong></strong>{{DOSSIER_ADRESSE}} {{DOSSIER_CP}} {{DOSSIER_VILLE}}</td></tr><tr><td><strong>Tél :</strong></td><td><strong></strong>{{DOSSIER_MOBILE}}</td></tr><tr><td><strong>Courriel&nbsp;:</strong></td><td>{{DOSSIER_EMAIL}}</td></tr><tr><td><strong>Intitulé de la formation suivie&nbsp;:</strong></td><td>{{DOSSIER_PARCOURS_INTITULE}} <strong><br></strong></td></tr></tbody></table><table><tbody><tr><td><br></td></tr></tbody></table></td></tr></tbody></table></td></tr><tr><td><br></td><td><p style=\"margin-bottom: var(--spacer-2);\">Article 1 : Objet</p><p style=\"margin-bottom: var(--spacer-2);\">En\r\n exécution du présent contrat, l’Organisme de Formation s’engage à \r\norganiser l’action de formation suivante : {{DOSSIER_PARCOURS_INTITULE}} <strong><br></strong><br>Le \r\nprésent contrat est conclu en application des dispositions du Livre III \r\nde la Sixième partie du Code du travail portant organisation de la \r\nformation professionnelle continue.</p></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><table><tbody><tr><td><table><tbody><tr><td colspan=\"2\"><p style=\"margin-bottom: var(--spacer-2);\">Article 2 : Nature et caractéristiques de l’action de formation</p><p style=\"margin-bottom: var(--spacer-2);\">L’action\r\n de formation entre dans la catégorie des actions de formation \r\nprofessionnelle tout au long de la vie, prévues par l’article L.6313-1 \r\ndu Code du travail.<br></p><p style=\"margin-bottom: var(--spacer-2);\">Le programme préparé est : {{PARCOURS_INTITULE_OFFICIEL}}-{{PARCOURS_CODE_RNCP}} de niveau {{PARCOURS_NIVEAU_SORTIE}}.</p><p style=\"margin-bottom: var(--spacer-2);\">Le programme est : {{DOSSIER_PARCOURSQUALIFIANTCERTIFIANT}}<br></p><p class=\"MsoNormal\" style=\"margin-bottom: var(--spacer-2); line-height: normal;\">L\'action de formation aura les dates suivantes&nbsp;:&nbsp;&nbsp;</p><br><table><tbody><tr><td><span style=\"line-height: 15.3333px;\">Date de début prévisionnelle de l’action&nbsp;:</span></td><td>&nbsp;{{DOSSIER_CALENDRIER_DATEDEBUT}}</td></tr><tr><td><span style=\"line-height: 15.3333px;\">Date de fin prévisionnelle de l’action&nbsp;:</span></td><td>{{DOSSIER_CALENDRIER_DATEFIN}}</td></tr><tr><td><span style=\"line-height: 15.3333px;\">Durée en centre&nbsp;:</span></td><td>{{DOSSIER_CALENDRIER_DUREE_CENTRE}}</td></tr><tr><td><span style=\"line-height: 15.3333px;\">Durée en entreprise&nbsp;:</span></td><td>{{DOSSIER_CALENDRIER_DUREE_ENTREPRISE}}</td></tr><tr><td><span style=\"line-height: 15.3333px;\">Durée totale&nbsp;:</span></td><td>{{DOSSIER_CALENDRIER_DUREE_TOTALE}}</td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr></tbody></table><p style=\"margin-bottom: var(--spacer-2);\">Les objectifs de l\'action de formation :&nbsp;</p><p style=\"margin-bottom: var(--spacer-2);\">{{PARCOURS_OBJECTIF}}</p></td></tr></tbody></table></td></tr></tbody></table></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><p style=\"margin-bottom: var(--spacer-2);\">Article 3 : Niveau de connaissances préalables nécessaire avant l’entrée en formation</p><p style=\"margin-bottom: var(--spacer-2);\">Afin\r\n de suivre au mieux l’action de formation susvisée et obtenir la ou les \r\nqualifications auxquelles elle prépare, le Stagiaire est informé qu’il \r\nest nécessaire de posséder, avant l’entrée en formation, les prérequis \r\nsuivants :<br>{{PARCOURS_PREREQUIS}}</p></td></tr></tbody></table></td></tr><tr><td><br></td></tr></tbody></table><table><tbody><tr><td><table><tbody><tr><td><p style=\"margin-bottom: var(--spacer-2);\">Article 4 : Organisation de l’action de formation</p><p style=\"margin-bottom: var(--spacer-2);\">Une\r\n convocation est envoyée la semaine précédant chaque session de \r\nformation avec une précision du lieu de formation en présentiel sur l\'un\r\n des sites de {{SOCIETE_RAISON_SOCIALE}} ou à distance, le cas échéant.<br>Rythme hebdomadaire : du lundi au vendredi, de 9h00 à 12h30 et de 13h30 à 17h00.<br></p><p style=\"margin-bottom: var(--spacer-2);\">Les modalités de réalisation :</p><ul><li>Présentiel ou distanciel : en groupe</li><li>Auto-formation : temps libre</li><li>E-Learning : temps libre</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Effectif : à déterminer</p></td></tr><tr><td><p class=\"MsoNormal\" style=\"margin-bottom: var(--spacer-2);\"><span style=\"line-height: 15.3333px;\">La répartition du temps de formation suit le modèle suivant&nbsp;:</span></p></td></tr></tbody></table></td></tr><tr><td><table><tbody><tr><td>Durée de formation en centre:&nbsp;<br></td><td>{{DOSSIER_CALENDRIER_DUREE_CENTRE}} heures<br></td></tr><tr><td>&nbsp;&nbsp; Dont en présentiel ou classe virtuelle en groupe:<br></td><td>{{DOSSIER_CALENDRIER_DUREE_PRESENTIELVIRTUEL}} heures</td></tr><tr><td>&nbsp;&nbsp; Dont en auto-formation (e-learning):<br></td><td>{{DOSSIER_CALENDRIER_DUREE_ELEARNING}} heures<br></td></tr><tr><td>&nbsp;&nbsp; Dont en soutien personnalisé:<br></td><td>{{DOSSIER_CALENDRIER_DUREE_SOUTIEN}} heures</td></tr><tr><td>&nbsp;&nbsp; Dont en remise à niveau:</td><td>{{DOSSIER_CALENDRIER_DUREE_TRAN}} heures</td></tr><tr><td>&nbsp;&nbsp; Dont en TRE:</td><td>{{DOSSIER_CALENDRIER_DUREE_TRE}} heures</td></tr><tr><td>&nbsp;&nbsp; Dont en coaching en insertion professionnelle:</td><td>{{DOSSIER_CALENDRIER_DUREE_COACHING}} heures</td></tr><tr><td>Durée en stage pratique:<br></td><td>{{DOSSIER_CALENDRIER_DUREE_ENTREPRISE}} heures</td></tr><tr><td>Durée totale:<br></td><td>&nbsp;{{DOSSIER_CALENDRIER_DUREE_TOTALE}} heures</td></tr></tbody></table></td></tr></tbody></table><table><tbody><tr><td><br></td></tr><tr><td><p style=\"margin-bottom: var(--spacer-2);\"><span style=\"line-height: 15.3333px;\">Le\r\n calendrier de formation ainsi que le programme détaillé sont présentés \r\nen annexe. Les dates et horaires de formation sont indicatifs et \r\nsusceptibles de modification, en fonction des évènements pouvant \r\nsurvenir au cours de la réalisation de l’action de formation.</span>&nbsp;<span style=\"line-height: 15.3333px;\">Dans ce cas, toutes les parties en seront informées.</span></p></td></tr></tbody></table><table><tbody><tr><td colspan=\"2\"><p style=\"margin-bottom: var(--spacer-2);\"><br></p><p style=\"margin-bottom: var(--spacer-2);\">Article 5 : Modalités d’évaluation et de sanction</p><p style=\"margin-bottom: var(--spacer-2);\">{{SOCIETE_RAISON_SOCIALE}} participera à l\'établissement du bilan des acquis professionnels \r\nconformément à la loi n° 2004-391 du 4 mai 2004 relative à la formation \r\nprofessionnelle tout au long de la vie et au dialogue social. En \r\napplication de l’article L.6353-1 du code du travail, une attestation \r\nmentionnant les objectifs, la nature et la durée de l’action ainsi que \r\nles résultats de l’évaluation des acquis de la formation sera remise au \r\nStagiaire à l’issue de la formation.</p><p style=\"margin-bottom: var(--spacer-2);\">Sanction de la formation :&nbsp;</p>{{PARCOURS_SANCTION_RESUME}}<br><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><table><tbody><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr></tbody></table></td><td><br></td></tr><tr><td><table><tbody><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr></tbody></table></td><td><br></td></tr><tr><td><table><tbody><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br></td><td><br></td></tr></tbody></table></td><td><br></td></tr><tr><td><br></td><td><br></td></tr><tr><td><br><br><p style=\"margin-bottom: var(--spacer-2);\">Article 6 : Règlement intérieur</p><p style=\"margin-bottom: var(--spacer-2);\">Durant les heures de cours, le Stagiaire devra se conformer à la discipline et au règlement intérieur de [[SOCIETE_RAISON_SOCIALE]]. Le non suivi de ce règlement pourra entraîner le renvoi de l\'établissement.</p></td><td><br></td></tr><tr><td><p style=\"margin-bottom: var(--spacer-2);\">Article 7 : Dispositions financières</p><p style=\"margin-bottom: var(--spacer-2);\">7.1 Prix De L’action De Formation<br>Le coût de la formation objet de cette convention, s’élève à :</p><table><tbody><tr><td>Coût Unitaire H.T. :</td><td>DEVIS_MONTANT_HT_CANDIDIATxxx</td><td>€</td></tr><tr><td>Durée Totale De La Formation :</td><td>[[ PLANNING_DUREExxx ]]</td><td>Heures</td></tr><tr><td>Montant H.T. :</td><td><p style=\"margin-bottom: var(--spacer-2);\">&nbsp;</p><p style=\"margin-bottom: var(--spacer-2);\">[[DOSSIERDEVIS_MONTANTHT]]</p></td><td>€</td></tr><tr><td>T.V.A.<br>&nbsp;</td><td>[[DOSSIERDEVIS_MONTANTTVA]]</td><td>€</td></tr><tr><td>Montant T.T.C. :</td><td>[[DOSSIERDEVIS_MONTANTTTC]]</td><td>€</td></tr></tbody></table><table><tbody><tr><td><br></td><td><br></td><td><br></td></tr></tbody></table><table><tbody><tr><td><br></td><td><table><tbody><tr><td>Frais De Dossier :</td><td>FRAIS***</td></tr></tbody></table></td></tr></tbody></table><table><tbody><tr><td>Kit Numérique :</td><td>KIT_NUM***</td></tr></tbody></table><p style=\"margin-bottom: var(--spacer-2);\"><br></p><p style=\"margin-bottom: var(--spacer-2);\">*[[SOCIETE_RAISON_SOCIALE]] est exonéré de TVA - Art. 261.4.4 du CGI.<span style=\"line-height: 12.84px;\"></span></p><p style=\"margin-bottom: var(--spacer-2);\">Dans le cas où une participation individuelle est prévue dans le dossier, le paiement de celle-ci est obligatoire et exigible au démarrage de l’action de formation. Cette participation est à la charge du Stagiaire, sauf mention contraire indiquée lors de la facturation. Les sommes versées ne pourront pas faire l’objet de remboursement, même en cas d’abandon de la formation par le Stagiaire.</p><table><tbody><tr><td><br></td><td><br></td><td>7.2 Passage devant le jury de validation<br>Le premier passage devant le jury de validation des compétences est facturé à xx €. En cas de non présentation du candidat ou d’échec partiel ou total aux examens de validations, le second passage sera facturé xxx € TTC à la charge du candidat.</td></tr><tr><td><br></td><td><br></td><td>7.3 Délai de rétractation<br>A compter de la date de signature du présent contrat, le Stagiaire a un délai de 10 jours pour se rétracter. Il doit en informer [[SOCIETE_RAISON_SOCIALE]] par lettre recommandée avec accusé de réception. Dans ce cas, aucune somme ne peut être exigée.<br></td></tr></tbody></table></td></tr><tr><td><p style=\"margin-bottom: var(--spacer-2);\">7.4 Modalités de paiement<br></p></td><td><br></td></tr><tr><td colspan=\"2\">La formation est financée par :<br>xxFinanceur 1<br>xxFinanceur 2<br><br>selon l\'échéancier suivant :<br><br><table><tbody><tr><td><span style=\"line-height: 15.3333px;\">Période de xx à</span></td><td>ER_echeancier1xxx]]</td></tr><tr><td><span style=\"line-height: 15.3333px;\">Période de xx à xx :</span></td><td>[[DOSSIER_echancier2xxx]]</td></tr><tr><td><span style=\"line-height: 15.3333px;\">Periode de xx à xx :</span></td><td>[[DOSSIER_DUREEENCENTRExxx]]</td></tr><tr><td><br></td><td><br></td></tr><tr><td><p style=\"margin-bottom: var(--spacer-2);\">Article 8 : Annulation de l\'action de formation</p><p style=\"margin-bottom: var(--spacer-2);\">8.1 Annulation, absence, report d’inscription de la part du stagiaire<br>Toute annulation d’inscription doit être signalée par téléphone et confirmée par écrit.<br>Une annulation intervenant plus de deux semaines avant le début du stage ne donnera lieu à aucune facturation.<br>Une annulation intervenant moins de 5 jours ouvrés avant le début du stage sera facturée à 50 % de la somme convenue initialement pour la session.<br>Une annulation intervenant la veille ou le jour-même du stage sera facturée à 100 %.<br>En cas d’absence ou d’abandon du Stagiaire, pour autre motif que la force majeure, la prestation sera facturée en totalité.<br>Si le Stagiaire est empêché de suivre la formation par suite d’un cas de force majeure dûment reconnue, le contrat de formation professionnelle est résilié. Dans cette hypothèse, seules les prestations dispensées sont dues au prorata temporis de leur valeur prévue au présent contrat.</p><p style=\"margin-bottom: var(--spacer-2);\">8.2 Annulation d’une session de formation de la part du dispensateur<br>[[SOCIETE_RAISON_SOCIALE]] se réserve la possibilité d’annuler tout stage en cas de manque de participations, de problème d’approvisionnement de supports de stage ou de problème technique et ce sans aucun dédommagement. Dans ce cas, les Stagiaires seront prévenus avant le début du stage et leur inscription automatiquement reportée à la session suivante. Aucun remboursement ne sera exigé si une solution de rattrapage est proposée par [[ SOCIETE_RAISON_SOCIALE]].</p></td><td><br></td></tr></tbody></table></td></tr></tbody></table><p style=\"margin-bottom: var(--spacer-2);\">Article 9 : Moyens permettant de suivre l’exécution de la formation</p><p style=\"margin-bottom: var(--spacer-2);\">Pour les séquences de formation à distance, l’Organisme de Formation s’engage à fournir au financeur :</p><ul><li>Une attestation d’assiduité signée de l’Organisme de Formation justifiant de la réalisation des travaux par le Stagiaire ainsi que du passage des tests, des évaluations intermédiaires et finales</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Pour les séquences de formation en présentiel, l’Organisme de Formation s’engage à fournir au financeur :</p><ul><li>La copie des feuilles d’émargement signées par journée par l’apprenant ainsi que par le(s) formateur(s)</li></ul><p style=\"margin-bottom: var(--spacer-2);\">En cas de contrôle :<br>Pour les séquences de formation à distance, l’Organisme de Formation s’engage à fournir au financeur :<br>Les justificatifs d’activités d’accompagnement ou d’assistance pédagogique et technique :</p><ul><li>Traces des échanges à distance : courriels, chat, forums, outils collaboratifs</li><li>Enregistrement des classes virtuelles</li><li>Relevés d’appels téléphoniques</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Les justificatifs de la réalisation et des résultats de la formation :</p><ul><li>Feuilles d\'émargement détaillées par 1/2 journée (séquences présentielles)</li><li>Récapitulatif des travaux réalisés par l\'apprenant / justificatifs de réalisation des travaux</li><li>Résultats aux tests et évaluations finales et intermédiaires / justificatifs de passage des tests</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Les factures :</p><ul><li>Dépenses directes</li><li>Factures d’achat ou de location des ressources pédagogiques et de la plate-forme</li><li>Factures de sous-traitance</li><li>Factures de documentations, matériels pédagogiques, locations de salles</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Article 10 : Les engagements réciproques</p><p style=\"margin-bottom: var(--spacer-2);\">L’Organisme de Formation s’engage à :</p><ul><li>Faire signer les feuilles de présence aux Stagiaires quotidiennement pour les séquences en présentiel.</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Accompagner pédagogiquement le Stagiaire dans sa progression durant la formation par :</p><ul><li>Un suivi régulier et accompagnement individuel</li><li>L’utilisation d’outils d’animation pédagogiques disponibles sur des plates-formes en fonction de la pertinence pédagogique et de la méthode</li><li>La mise en place des regroupements, si prévu dans le programme</li><li>La mise en place d’outils d’évaluation des acquis tout au long de la formation</li><li>Conserver les éléments liés à la réalisation de chaque module (travaux réalisés, copies des courriers électroniques…) pour attester du suivi de l’action.</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Ces éléments doivent être tenus à la disposition du financeur en cas de contrôle.</p><p style=\"margin-bottom: var(--spacer-2);\">Le Stagiaire s’engage à :</p><ul><li>Respecter le planning global reçu</li><li>Signer les feuilles de présence par demi-journée quotidiennement (séquences présentielles)</li><li>Participer aux regroupements, si prévu dans le programme</li><li>Réaliser les travaux et produire les documents demandés</li><li>Ne pas transmettre l’accès aux supports pédagogiques à l’extérieur et ne pas diffuser son mot de passe à des personnes étrangères à la convention</li><li>Communiquer sur demande du financeur, tout document justifiant de la réalité et de la qualité de la formation</li><li>Se conformer à la discipline et au règlement intérieur de l’OF</li></ul><p style=\"margin-bottom: var(--spacer-2);\">Le non suivi de ce règlement pourra entrainer le renvoi de l’établissement.</p><p style=\"margin-bottom: var(--spacer-2);\">Article 11 : Protection des données à caractère personnel</p><p style=\"margin-bottom: var(--spacer-2);\">Le traitement des données à caractère personnel de [[DOSSIER_NOMCOMPLET]] est nécessaire à l\'exécution<br>du contrat, et le cas échéant à l\'envoi d\'informations commerciales. [[SOCIETE_RAISON_SOCIALE]] s\'engage, dans le cadre de ses activités et conformément à la législation en vigueur en France (Loi n\" 78-Oll du 6 janvier 1978 relative à l\'informatique, aux fichiers et aux libertés (dite Loi informatique et Libertés) et en Europe (Règlement (UEl2016/679 du Parlement européen et du Conseil du 27 avril 2016 relatif à la protection des personnes physiques à l\'égard du traitement des données à caractère personnel et à la libre circulation de ces données (RGPD), à assurer la protection, la confidentialité et la sécurité des données à caractère personnel des personnes bénéficiant des services de [[SOCIETE_RAISON_SOCIALE]] , ainsi qu\'à respecter leur vie privée.<br><br>[[SOCIETE_RAISON_SOCIALE]]&nbsp; prend toutes les mesures raisonnables pour s\'assurer de l\'exactitude et de la pertinence des données personnelles au regard des finalités pour lesquelles elle les traite, conformément au Règlement européen sur la protection des données précité. [[SOCIETE_RAISON_SOCIALE]]&nbsp; ne conserve pas les données à caractère personnel de&nbsp;[[DOSSIER_NOMCOMPLET]] au-delà de la durée nécessaire pour atteindre la finalité du traitement, tout en respectant les limites légales et règlementaires applicables ou une autre durée compte tenu des contraintes opérationnelles telle qu\'un management efficace de la relation clients et les réponses aux demandes en justice ou des autorités de contrôle dont [[SOCIETE_RAISON_SOCIALE]] dépend.<br><br>Conformément à la réglementation européenne en vigueur,&nbsp;[[DOSSIER_NOMCOMPLET]] dispose des droits suivants:<br>droit d\'accès (article 15 RGPD) et de rectification (article 16 RGPD), de mise à jour, de complétude des données<br>droit d\'effacement des données à caractère personnel (article 17 du RGPD),<br>droit de retirer à tout moment un consentement (article 13-2c RGPD),<br>droit à la limitation du traitement des données (article 18 RGPD),<br>droit d\'opposition au traitement des données (article 21 RGPD),<br>droit à la portabilité des données fournies, lorsque ces données font l\'objet de traitements automatisés fondés sur leur consentement ou sur un contrat (article 20 RGPD).<br>[[DOSSIER_NOMCOMPLET]] est toutefois informé que les données à caractère personnel collectée sont, le cas échéant, nécessaires à l\'exécution de la prestation délivrée par [[SOCIETE_RAISON_SOCIALE]], de sorte qu\'en cas d\'usage de son droit d\'effacement desdites données, d\'opposition ou de limitation des traitements avant le terme de la relation contractuelle, la prestation ne pourra pas être exécutée correctement. Ces droits peuvent être exercés en adressant un courrier à [[SOCIETE_RAISON_SOCIALE]], à l\'attention du DPO, [[SOCIETE_RAISON_SOCIALE]], [[SOCIETE_ADRESSE]] [[SOCIETE_CP]] [[SOCIETE_VILLE]]. [[SOCIETE_RAISON_SOCIALE]] répond à la personne ayant fait l\'usage<br>d\'un des droits susvisés dans un délai d\'un (1) mois à compter de la réception de la demande. Ce délai peut<br>néanmoins être prolongé de deux (2) mois, compte tenu de la complexité et du nombre de demandes. Dans<br>cette hypothèse, [[SOCIETE_RAISON_SOCIALE]] informera la personne concernée de cette prolongation dans le délai d\'un (1) mois à compter de la réception de la demande.<br><br>[[DOSSIER_NOMCOMPLET]]&nbsp; a la possibilité d\'introduire une réclamation auprès de la Commission Nationale de l\'informatique et des Libertés ou de l\'autorité de contrôle de l\'État membre de l\'Union européenne dans lequel elle réside et de former un recours juridictionnel.<br>[[DOSSIER_NOMCOMPLET]] autorise [[SOCIETE_RAISON_SOCIALE]] à faire appel à des sous-traitants pour mener des activités de traitement de données à caractère personnel de&nbsp;[[DOSSIER_NOMCOMPLET]]&nbsp; strictement nécessaires à l\'exécution des prestations définies aux présentes.<br></p><p style=\"margin-bottom: var(--spacer-2);\">Article 12 : Cas De Différend</p><p style=\"margin-bottom: var(--spacer-2);\">Si une constatation ou un différend n’ont pu être réglés à l’amiable, le tribunal de commerce du siège de [[SOCIETE_RAISON_SOCIALE]] sera seul compétent pour régler le litige.</p><p style=\"margin-bottom: var(--spacer-2);\">Fait en double exemplaire le DATE_SIGNATURExxx, le xxx.<br></p><table><tbody><tr><td><table><tbody><tr><td><p style=\"margin-bottom: var(--spacer-2);\">Signature du Stagiaire</p><p style=\"margin-bottom: var(--spacer-2);\">[[DOSSIER_NOMCOMPLET]]&nbsp;</p></td><td><p style=\"margin-bottom: var(--spacer-2);\"><br></p><br><p style=\"margin-bottom: var(--spacer-2);\"><br></p><p style=\"margin-bottom: var(--spacer-2);\"><br></p><p style=\"margin-bottom: var(--spacer-2);\"><br></p></td><td><p style=\"margin-bottom: var(--spacer-2);\">Signature et cachet de l’École</p><p style=\"margin-bottom: var(--spacer-2);\">[[SOCIETE_RESPONSABLEECOLE_CIVILITE]][[SOCIETE_RESPONSABLEECOLE_NOMCOMPLET]],<br>[[SOCIETE_RESPONSABLEECOLE_QUALITE]] [[SOCIETE_CACHET]]</p></td></tr></tbody></table></td><td><br></td></tr><tr><td><br></td><td><br></td></tr></tbody></table><p style=\"margin-bottom: var(--spacer-2);\">En Annexe :</p><ul><li>Calendrier de formation</li><li>Programme détaillé</li></ul>', NULL, NULL, '2024-04-17 16:38:56', '2024-11-04 11:14:13', 0, NULL, NULL),
(50, 1, 1, NULL, NULL, 567, 572, 2, 'Notice légale sur la protection des données personnelles', 'Notice légale sur la protection des données personnelles', 'notice_legale', 'Notice légale sur la protection des données personnelles', NULL, NULL, '0', '<h2>Informations sur vos données personnelles</h2><h3><strong>1. NOTICE LEGALE</strong></h3><p>Conformément aux dispositions de l\'article 6-III-1 de la loi n° 2004-575 du 21 juin 2004 pour la confiance dans l\'économie numérique, nous vous informons que :</p><p>- le présent site est la propriété de, et est édité par {{ SOCIETE_RAISON_SOCIALE }}, Siret 389 385854000 54, et dont le siège social est fixé au 10-12, rue Planchat, 75020 PARIS, France , Tél +33(0)1 55 25 28 00;</p><p>- la directrice de publication du site web est {{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }}, en qualité de {{ SOCIETE_RESPONSABLE_QUALITE }} de {{ SOCIETE_RAISON_SOCIALE }}.</p><p>- le prestataire assurant le développement, la maintenance et le stockage direct et permanent est la société LMOSOFT, 199, rue Hélène Boucher, 34170 Castelnau-le-lez France - Tél + 33 (0)610771283, <a href=\"https://www.lmosoft.com/\">https://www.lmosoft.com/</a></p><p>&nbsp;</p><h3><strong>2. CONDITIONS D\'UTILISATION DU SITE</strong></h3><p><strong>Responsabilité</strong></p><p>L\'utilisateur reconnaît avoir pris connaissance des présentes conditions d\'utilisation et s\'engage à les respecter.</p><p>L\'utilisateur du site web reconnaît disposer de la compétence et des moyens nécessaires pour accéder et utiliser ce site.</p><p>L\'utilisateur de l\'espace personnel MyCommunity reconnaît avoir vérifié que la configuration informatique utilisée ne contient aucun virus et qu\'elle est en parfait état de fonctionnement.</p><p>L\'exploitant du site web met tout en œuvre pour offrir aux utilisateurs des informations et/ou outils disponibles et vérifiés. Il ne saurait cependant être tenu pour responsable des erreurs, d\'une absence de disponibilité des informations et/ou de la présence de virus sur son site.</p><p>Les informations fournies par l\'exploitant du site web le sont à titre indicatif. Elles ne sauraient dispenser l\'utilisateur d\'une analyse complète et personnalisée. L\'exploitant du site web ne saurait garantir l\'exactitude, la complétude, l\'actualité des informations diffusées sur son site.</p><p>En conséquence, l\'utilisateur reconnaît utiliser ces informations sous sa responsabilité exclusive</p><p><strong>Vie Privée Et Propriété Intellectuelle</strong></p><p>Les utilisateurs du site web de l\'espace personnel MyCommunity sont tenus de respecter les dispositions de la loi du 6 janvier 1978 relative à l\'Informatique, aux fichiers et aux libertés modifiée, dont la violation est passible de sanctions pénales.</p><p>Ils doivent notamment s\'abstenir, s\'agissant des données à caractère personnel auxquelles ils accèdent, de toute collecte, de toute utilisation détournée et, d\'une manière générale, de tout acte susceptible de porter atteinte à la vie privée ou à la réputation de ces personnes.</p><p>L\'espace personnel MyCommunity relève de la législation française sur le droit d’auteur et la propriété intellectuelle. Tous les droits de reproduction sont réservés, y compris pour les documents téléchargeables et les représentations iconographiques et photographiques.</p><p>La structure générale, ainsi que les logiciels, textes, images animées ou non, sons, savoir-faire et tous les autres éléments composant le site sont la propriété exclusive de l\'exploitant du site web.</p><p>Toute représentation totale ou partielle de ce site par quelque société que ce soit, sans l\'autorisation expresse de l\'exploitant du site, est interdite et constituerait une contrefaçon sanctionnée par les articles L. 335-2 et suivants du Code de la propriété intellectuelle.</p><p>Il en est de même des bases de données figurant sur l\'espace personnel MyCommunity, qui sont protégées par les dispositions de la loi du 1er juillet 1998 portant transposition, dans le Code de la propriété intellectuelle, de la directive européenne du 11 mars 1996 relative à la protection juridique des bases de données.</p><p>Les marques de l\'exploitant du site web et de ses partenaires, ainsi que les logos figurant sur le site, sont des marques semi-figuratives ou non et sont déposées.</p><p>Toute reproduction totale ou partielle de ces marques ou de ces logos, effectuée à partir des éléments du site, sans l\'autorisation expresse de l\'exploitant du site web, est donc prohibée au sens de l\'article L. 713-2 du Code de la propriété intellectuelle. Toute reproduction devra recevoir l’autorisation expresse de la directrice de publication.</p><p><strong>Liens</strong></p><p>Les utilisateurs et visiteurs du site web ne peuvent mettre en place un lien hypertexte en direction de ce site sans l\'autorisation expresse et préalable de l\'exploitant du site web.</p><p>L\'exploitant du site web ne saurait être responsable de l\'accès par les utilisateurs, via les liens hypertextes mis en place dans le cadre du site web, en direction d\'autres ressources présentes sur le réseau internet.</p><p>&nbsp;</p><p><strong>Lettres D’informations Électroniques</strong></p><p>Vous pouvez choisir de ne plus recevoir les « newsletters » publiées par {{ SOCIETE_RAISON_SOCIALE }} en vous reportant à la rubrique « mes abonnements » de votre profil ou en cliquant sur le lien de désabonnement figurant sur chaque newsletter.</p><p>&nbsp;</p><h3><strong>3. DONNÉES PERSONNELLES</strong></h3><p>{{ SOCIETE_RAISON_SOCIALE }} s\'engage à ce que la collecte et le traitement de vos données, effectués à partir de l\'espace personnel MyCommunity ou dans le cadre des activités de l’association soient conformes à la loi Informatique et Libertés et au règlement européen des données personnelles.</p><p>Pour toute information sur la protection des données personnelles, vous pouvez également consulter le site de la Commission Informatique et Liberté <a href=\"http://www.cnil.fr/\">www.cnil.fr</a></p><p>&nbsp;</p><p><strong>Identité Du Responsable Du Traitement :</strong></p><p>Les données personnelles sont collectées par : {{ SOCIETE_RAISON_SOCIALE }}, Siret 389 385854000 54, et dont le siège social est fixé au 10-12, rue Planchat, 75020 PARIS, France , Tél +33(0)1 55 25 28 00;</p><p>&nbsp;</p><p><strong>Finalité – Exploitation Des Données Vous Concernant :</strong></p><p>Les informations recueillies sont nécessaires pour votre adhésion et la bonne gestion des activités menées par {{ SOCIETE_RAISON_SOCIALE }}, dans le cadre de son activité, selon les finalités suivantes :</p><ul><li>Enregistrement et mise à jour des informations individuelles nécessaires à la gestion administrative des membres et donateurs, en particulier la gestion des cotisations, conformément aux dispositions statutaires qui régissent les intéressés ;</li><li>Établir, pour répondre à des besoins de gestion, des états statistiques ou des listes de membres ou de contacts, notamment en vue d\'adresser bulletins, convocations, invitations, journaux conformément à l’objet de l’association ;</li><li>Établir un annuaire d\'anciens élèves de {{ SOCIETE_RAISON_SOCIALE }} et des étudiants en cours de scolarité ;</li><li>Publication et envoi du magazine de l’association ;</li><li>Effectuer par tout moyen de communication des opérations relatives à des actions de prospection auprès des membres, donateurs et prospects ;</li><li>Effectuer des actions de communication conjointes avec les partenaires de l’école ou de l\'Organisme de Formation {{ SOCIETE_RAISON_SOCIALE }};</li><li>Traitement des données de connexion de l\'espace personnel MyCommunity à des fins purement statistiques.</li></ul><p><strong>Types De Données Collectées :</strong></p><p>{{ SOCIETE_RAISON_SOCIALE }} ne collecte que les données strictement nécessaires pour les besoins de son activité. Les données à caractère personnel collectées peuvent être les suivantes :</p><ul><li>Diplôme,</li><li>Matricule,</li><li>État civil nom, prénom, date et lieu de naissance,</li><li>Situation familiale,</li><li>Nationalité,</li><li>Coordonnées personnelles (adresse, mail, tel…),</li><li>Coordonnées professionnelles (fonction, entreprise, mandat, etc.),</li><li>Photographie,</li><li>Curriculum vitae (formations, fonctions professionnelles, loisirs),</li><li>Informations relatives à la gestion administrative de l’école (coordonnées bancaires, état des cotisations) ;</li><li>Informations relatives à la gestion des dons (coordonnées bancaires notamment) ;</li><li>Données de connexion (date, heure, adresse IP de l’ordinateur visiteur, page consultée)</li></ul><p>Le caractère obligatoire ou facultatif des données vous est signalé lors de la collecte par une astérisque. Certaines données sont collectées automatiquement du fait de vos actions sur le site.</p><p><strong>Origine Des Données :</strong></p><p>Les données à caractère personnel agrégées dans la base {{ SOCIETE_RAISON_SOCIALE }} proviennent de deux origines différentes :</p><p>- Informations fournies par vos soins :</p><ul><li>Via l’interface personnelle de chaque étudiant/stagiaire ou alumni de l\'espace personnel MyCommunity ;</li><li>Via la fiche de mise à jour jointe aux courriers d’appel de cotisation et d’invitation à l’assemblée générale ;</li></ul><p>- Collecte indirecte via des tiers :</p><ul><li>Relances téléphoniques ou mails par nos équipes,</li><li>Données sites professionnels type Linkedin.</li></ul><p><strong>Transferts De Données À Caractère Personnel Envisagés À Destination D\'un État Non Membre De L’Union Européenne :</strong></p><p>A ce jour, {{ SOCIETE_RAISON_SOCIALE }} ne transfère, ni n’envisage aucun transfert de vos données à caractère personnel à destination d’un état non membre de l’Union européenne.</p><p><strong>Durée De Conservation Des Catégories De Données Traitées :</strong></p><p>Les données à caractère personnel vous concernant recueillies par {{ SOCIETE_RAISON_SOCIALE }} sont transmises et conservées selon des protocoles sécurisés. Nous ne conservons pas vos données au-delà de la durée strictement nécessaire à l’accomplissement des finalités se rattachant à l’objet statutaire de l’association dans le but de créer et d’entretenir des relations entre les alumni de {{ SOCIETE_RAISON_SOCIALE }} et d’utiliser ces relations dans le domaine professionnel (édition de l’annuaire, du magazine, communications relatives à {{SOCIETE_RAISON_SOCIALE }} et son réseau),</p><p>&nbsp;</p><p><strong>Destinataires Des Données :</strong></p><p>Les destinataires des données collectées par {{SOCIETE_RAISON_SOCIALE }} sont les suivants : <strong>Salariés De L’école </strong>dans le cadre de leurs fonctions ; <strong>Délégués </strong>de promotion, de groupe professionnel, international, régional, socio-culturel, etc. ; <strong>Prestataires</strong> de l’école ; <strong>Partenaires</strong> de l’école (Pôle Emploi si pertinent, Conseil Régional Ile-de-France, Europe).</p><p>&nbsp;</p><p><strong>La Protection De Vos Données :</strong></p><p>{{SOCIETE_RAISON_SOCIALE }} et son prestataire technique protègent la sécurité de vos données à caractère personnel.</p><p>L\'espace personnel MyCommunity utilise la technologie SSL (Secure Sockets Layer Software) qui crypte les informations que vous entrez avant qu\'elles ne nous soient envoyées.</p><p>Nous maintenons en interne des mesures de sécurité électroniques et organisationnelles en rapport avec la collecte, la conservation et la communication des données concernant les candidats/stagiaires/étudiants et alumni.</p><p>&nbsp;</p><p><strong>Vos Droits Au Regard De La Loi Informatique Et Libertés :</strong></p><p>Conformément à la loi Informatique et Libertés du 6 janvier 1978, vous disposez d\'un droit d\'accès et de rectification et d’opposition aux données à caractère personnel vous concernant.</p><p>Si vous souhaitez exercer ce droit et obtenir communication des informations vous concernant, veuillez-vous adresser à {{SOCIETE_RAISON_SOCIALE }} dont l’adresse est la suivante : 10-12, rue Planchat, 75020 PARIS, France, ou par courrier électronique à : <a href=\"mailto:mediateur@doranco.fr\">mediateur@doranco.fr</a></p><p>Conformément à la réglementation en vigueur, votre demande doit être signée et accompagnée de la photocopie d’un titre d’identité portant votre signature et préciser l’adresse à laquelle doit vous parvenir la réponse. Une réponse vous sera alors adressée dans les meilleurs délais et en tout état de cause dans un délai d’un mois suivant la réception de la demande.</p><p>&nbsp;</p><p><strong>Sort De Vos Données Après Votre Décès :</strong></p><p>Le nouvel article 40-1 de la loi Informatique et libertés permet aux personnes de donner des directives relatives à la conservation, à l\'effacement et à la communication de leurs données après leur décès.</p><p>Vous pouvez-prendre connaissance de la procédure relative à ces directives en suivant le lien suivant : « https://www.cnil.fr/fr/ce-que-change-la-loi-pour-une-republique-numerique-pour-la-protection-des-donneespersonnelles#mortnumerique »</p><p>&nbsp;</p><p><strong>Cookies</strong></p><p>Vous êtes informé que, lors de vos visites sur de l\'espace personnel MyCommunity, un cookie peut, le cas échéant, s\'installer automatiquement sur votre logiciel de navigation. Le cookie est un fichier de taille réduite stocké sur votre ordinateur. En tant que tel, il s’agit d’un bloc de données qui ne permet pas d\'identifier les utilisateurs mais sert à enregistrer les informations relatives à la navigation de celui-ci sur le site. Les cookies sont utilisés d’une part, pour faciliter votre navigation sur le site et d’autre part, dans un but statistique.</p><p>Afin de mieux connaître la fréquentation du site nous faisons mesurer (principalement) le nombre de pages vues, de visiteurs, de visites ainsi que l’activité des visiteurs sur notre site et leur fréquence de retour.</p><p>Le paramétrage du logiciel de navigation permet d\'informer de la présence de cookies et éventuellement de la refuser de la manière décrite à l\'adresse suivante \" http://www.cnil.fr/vos-libertes/vos-traces/les-cookies/ \".</p><p>Vous disposez d\'un droit d\'accès, de retrait et de modification des données à caractère personnel communiquées par le biais des cookies dans les conditions indiquées ci-dessus.</p><p>Signature du stagiaire<br>&nbsp;</p><p>Le DATE_SIGNATURE</p><p>&nbsp;</p><p>{{ DOSSIER_NOMCOMPLET }}</p><p>SIGNATURE_DOSSIER</p>', NULL, NULL, '2024-04-17 16:40:14', '2024-04-17 16:40:50', 0, NULL, NULL),
(51, 1, 1, NULL, 39, 566, 572, 2, 'Déclaration d\'abandon', 'Déclaration d\'abandon', 'lettre_abandon_formation', 'Déclaration d\'abandon', NULL, NULL, '0', '<p>A {{ SOCIETE_VILLE }}, le {{DATE}}</p><p>A l\'attention de {{ DOSSIER_CIVILITE }} {{ DOSSIER_NOMCOMPLET }},<br>né(e) le : {{ DOSSIER_DATENAISSANCE }} et demeurant {{ DOSSIER_ADRESSE }} - {{ DOSSIER_CP }} {{ DOSSIER_VILLE }}</p><p><strong>Objet : Déclaration D\'abandon De Formation</strong></p><p>Je soussigné(e) {{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }}, {{ SOCIETE_RESPONSABLE_QUALITE }} de {{ SOCIETE_RAISON_SOCIALE }}, déclare que :</p><p>{{ DOSSIER_CIVILITE }} {{ DOSSIER_NOMCOMPLET }} inscrit(e) en formation {{ PARCOURS_INTITULE }} initialement prévue du {{ PLANING_DATEDEBUT }} au {{ PLANING_DATEFIN }}</p><p>déclare abandonner la formation en date du dateabondantformation.</p><p>RÉSULTAT DE L’ÉVALUATION DES ACQUIS : non acquis</p><p>NOM ET ADRESSE DE L’ORGANISME DISPENSANT LA FORMATION :</p><p>{{ SOCIETE_RAISON_SOCIALE }}<br>{{ SOCIETE_ADRESSE }}<br>{{ SOCIETE_CP }} {{ SOCIETE_VILLE }}</p><p>Fait pour valoir ce que de droit à {{ SOCIETE_VILLE }}, le {{DATE}}.</p><p>{{ SOCIETE_RESPONSABLE_CIVILITE }} {{ SOCIETE_RESPONSABLE_NOMCOMPLET }}<br>{{ SOCIETE_RESPONSABLE_QUALITE }}</p><p>{{ SOCIETE_CACHET }}</p>', NULL, NULL, '2024-04-17 16:43:43', '2024-04-17 16:52:00', 0, NULL, NULL),
